mapper.update:
update shopping_cart set number = #{number}
where
    user_id = #{userId}

        and dish_id = #{dishId}


        and setmeal_id = #{setmealId}


        and dish_flavor = #{dishFlavor}

mapper.delete:
delete from shopping_cart
where user_id = #{userId}

    and dish_id = #{dishId}


    and setmeal_id = #{setmealId}


    and dish_flavor = #{dishFlavor}

mapper.select:
select * from shopping_cart

    user_id = #{userId}

        and dish_id = #{dishId}


        and setmeal_id = #{setmealId}


        and dish_flavor = #{dishFlavor}
